    path_info = {
    "LearningPath1": {"title": "生成AIの未来を探る", "Type": "テクノロジー"},
    "LearningPath2": {"title": "ビジネススキルを磨く", "Type": "ビジネス"},
    "LearningPath3": {"title": "脳と集中力の科学", "Type": "健康"},
    "LearningPath4": {"title": "グローバルビジネスの基礎", "Type": "国際"},
    "LearningPath5": {"title": "学び直しのススメ", "Type": "教育"},
    "LearningPath6": {"title": "テクノロジーと社会", "Type": "工学"},
    "LearningPath7": {"title": "ニュースで学ぶ社会", "Type": "社会"},
    "LearningPath8": {"title": "スキルで年収アップ", "Type": "キャリア"},
    "LearningPath9": {"title": "教育の未来", "Type": "教育"},
    "LearningPath10": {"title": "科学と日常", "Type": "科学"}
    }

    for folder, info in path_info.items():
        os.makedirs(folder, exist_ok=True)
        filepath = os.path.join(folder, "PathTitle.txt")
        with open(filepath, "w", encoding="utf-8") as f:
            f.write(f"title: {info['title']}\n")
